ontent','留言内容不能为空。','2',NULL,'lang'),(134,'浏览留言','./','_self','0','topbar'),(135,'发布留言','post.php','_self','1','topbar');

/*Table structure for table `introduce` */

DROP TABLE IF EXISTS `introduce`;

CREATE TABLE `introduce` (
  `IntroduceID` int(11) NOT NULL AUTO_INCREMENT,
  `content` text CHARACTER SET gbk NOT NULL,
  `categoryID` int(10) unsigned NOT NULL,
  `title` varchar(150) CHARACTER SET gbk COLLATE gbk_bin DEFAULT NULL,
  PRIMARY KEY (`IntroduceID`),
  UNIQUE KEY `categoryID` (`categoryID`)
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=latin1;

/*Data for the table `introduce` */

insert  into `introduce`(`IntroduceID`,`content`,`categoryID`,`title`) values (1,'<p>\r\n	<span class=\"Apple-style-span\" style=\"background-color: rgb(255, 255, 255);\"><b>机构设置&nbsp;</b></span>内容更新中...</p>\r\n',3,'机构设置'),(2,'<p>内容完善中...</p>',22,'内容完善'),(3,'<p>&nbsp;</p>\r\n<div><span style=\"font-family: Tahoma;\"><span style=\"font-size: medium;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 海南大学三亚学院管理分院成立于2010年1月，是由原经济管理分院拆分而成立的。分院现设有市场营销、工商管理、工程管理、信息管理与信息系统和人力资源管理五个本科专业，目前管理分院共有在校本科生2000余人。</span></span></div>\r\n<div><span style=\"font-family: Tahoma;\"><span style=\"font-size: medium;\">&nbsp;</span></span></div>\r\n<div><span style=\"font-family: Tahoma;\"><span style=\"font-size: medium;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 管理分院是一个具有丰富经验和活力的办学团队，现有教职工60余人。专职教师中具有教授、副教授职称的教师占29%，讲师和博士占38%，其余为具有硕士 研究生学历的青年教师。分院目前已经形成了一支学历较高、职称年龄结构相对合理、具有较好的能力和素质的师资队伍。分院坚持学院的办学图景、理念和价值， 坚持以育人为根本任务，以教学为中心，以科研为基础，不断提高教学质量，培养掌握一定专业知识和技能，具有创新精神、实践能力和健康人格的应用型人才。</span></span></div>\r\n<div><span style=\"font-family: Tahoma;\"><span style=\"font-size: medium;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 在教学方面，分院提倡大胆开展教学改革，不断创新教学理念和方法；提倡以情景教学、案例教学为主的互动式教学方式；更新教学观念，改革教学内容、教学形 式、教学方法手段，加强实践性教学，受到学生普遍欢迎。在科研上，已发表多篇论文和著作，获得省社科联、教育厅和学校多个科研项目,有的论文被海南省社科 联评为一等奖。</span></span></div>\r\n<div><span style=\"font-family: Tahoma;\"><span style=\"font-size: medium;\">&nbsp;&nbsp; </span></span></div>\r\n<div><span style=\"font-family: Tahoma;\"><span style=\"font-size: medium;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 管理分院紧抓高等教育大众化的契机和社会转型时期细分的市场对于人才的需求，进行人才培养模式的革新，使人才培养既符合市场具有&ldquo;上岗&rdquo;竞争力的&ldquo;工具性 导向&rdquo;实用性人才的需求，又符合人的全面、可持续、&ldquo;上梯阶&rdquo;发展的&ldquo;超功利导向&rdquo;的健康人格指向。管理分院进行教学计划修订和课程改革，强化学生对外 语、计算机、中文应用写作三门交往工具的掌握；贯彻&ldquo;三级实习&rdquo;，建立不同时段、不同层次的实训和毕业前的就业再培训机制，培养学生&ldquo;用得着、带得走、接 得上&rdquo;的专业知识和专业技能；强化公共政治课程、班级文化建设、宿舍文化建设，打通校内外实践；促使学生了解国情、懂得趋势、习惯礼仪、能够沟通，引导学 生积累社会资本；致力于把学生培养成具有创新精神、学习能力和实践能力的应用型、实用性高级专门人才。 </span></span></div>\r\n<div><span style=\"font-family: Tahoma;\"><span style=\"font-size: medium;\">&nbsp;</span></span></div>\r\n<div><span style=\"font-family: Tahoma;\"><span style=\"font-size: medium;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 管理学院为增强学生动手，培养实践技能打造良好平台。现有一个管理综合实验室、多个校外实习基地。管理分院学生为丰富业余学习生活积极开展第二课堂活动，先后获得了多个奖项。这些为学生健康的体魄和健康的人格培养提供了优良的土壤。</span></span></div>\r\n<div><span style=\"font-family: Tahoma;\"><span style=\"font-size: medium;\">&nbsp;</span></span></div>\r\n<div><span style=\"font-family: Tahoma;\"><span style=\"font-size: medium;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 面对新的发展形势，管理分院紧紧围绕 &ldquo;走进校园是为了更好地走向社会&rdquo; 这一崭新的育人理念，秉承&ldquo;自立更生，合力更新，有容乃大，不懈则优&rdquo;的校训和&ldquo;豪气，大气，正气&rdquo;的院风，按照311培养的培养模式，我院将加强学科建 设和师资队伍的建设，强化规范管理、优质服务，打造学生在德、智、体方面全面发展的良好平台，培养出建设祖国的栋梁人才。</span></span></div>\r\n<div><span style=\"font-family: Tahoma;\"><span style=\"font-size: medium;\">&nbsp;&nbsp;&nbsp; </span></span></div>\r\n<div><span style=\"font-family: Tahoma;\"><span style=\"font-size: medium;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 教育改变命运，大学提供阶梯。成千上万的年轻学子走进三亚学院管理分院，会在全新成长理念的熏陶下，焕发自身的学习兴趣和热情，养成适应社会进步和先进文化发展的高品质，真正地愉快学习、自主成长，更好地走上社会。</span></span></div>\r\n<div><span style=\"font-family: Tahoma;\"><span style=\"font-size: medium;\">&nbsp;</span></span></div>\r\n<div><span style=\"font-family: Tahoma;\"><span style=\"font-size: medium;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 三亚学院管理分院</span></span></div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>',128,'title'),(4,'<div style=\"text-indent: 20.65pt\"><span style=\"font-size: small\"><span style=\"font-family: Tahoma\">\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; text-indent: 20.65pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><b style=\"mso-bidi-font-weight: normal\"><span style=\"font-family: 宋体; font-size: 14pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">王毅武</span></b><b style=\"mso-bidi-font-weight: normal\"><span lang=\"EN-US\" style=\"font-family: Tahoma; font-size: 14pt; mso-font-kerning: 0pt\">&nbsp;</span></b><span lang=\"EN-US\" style=\"font-family: Tahoma; font-size: 14pt; mso-font-kerning: 0pt\">&nbsp; <span style=\"mso-spacerun: yes\">&nbsp;&nbsp;</span></span><span style=\"font-family: 宋体; font-size: 14pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">教授</span><span lang=\"EN-US\" style=\"font-family: Tahoma; font-size: 14pt; mso-font-kerning: 0pt\">&nbsp; </span><span style=\"font-family: 宋体; font-size: 14pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">博士生导师</span><span lang=\"EN-US\" style=\"font-family: Tahoma; font-size: 14pt; mso-font-kerning: 0pt\"><span style=\"mso-spacerun: yes\">&nbsp;&nbsp; </span><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; text-indent: 20.65pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span style=\"font-family: 宋体; font-size: 14pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">现任三亚学院管理分院院长</span><span lang=\"EN-US\" style=\"font-family: Tahoma; font-size: 14pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; text-indent: 20.65pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span lang=\"EN-US\" style=\"font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p>&nbsp;</o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 16.3pt; text-indent: 20.4pt; margin: 0cm 0cm 0pt; word-break: break-all; mso-pagination: widow-orphan\"><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">1948</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">年出生，山东龙口人。博士、教授、博士生导师，国务院特殊津贴专家（</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">1992</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">年）、国家级有突出贡献专家（</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">1992</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">年）</span><span style=\"font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">。</span><span lang=\"EN-US\" style=\"font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt\"><a target=\"_blank\" href=\"http://baike.baidu.com/view/1264.htm\"><span lang=\"EN-US\" style=\"font-family: 宋体; color: windowtext; text-decoration: none; text-underline: none; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma\"><span lang=\"EN-US\">武汉大学</span></span></a></span><span style=\"font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">经济学系毕业，曾任</span><span lang=\"EN-US\" style=\"font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt\"><a target=\"_blank\" href=\"http://baike.baidu.com/view/120960.htm\"><span lang=\"EN-US\" style=\"font-family: 宋体; color: windowtext; text-decoration: none; text-underline: none; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma\"><span lang=\"EN-US\">北京师范大学珠海分校</span></span></a></span><span style=\"font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">国际管理学院执行院长。现任</span><span lang=\"EN-US\" style=\"font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt\"><a target=\"_blank\" href=\"http://baike.baidu.com/view/39457.htm\"><span lang=\"EN-US\" style=\"font-family: 宋体; color: windowtext; text-decoration: none; text-underline: none; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma\"><span lang=\"EN-US\">海南大学</span></span></a></span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">中国现代经济理论研究所所长，政协海南省委常委、经济委员会副主任，中国经济思想史学会副会长、中国马克思主义经济学说史学会副会长、中国《资本论》研究会常务理事、中国经济发展研究会常务理事、中华外国经济学说研究会理事等。在省级以上学术刊物发表学术论文</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">270</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">多篇，已公开出版学术著作、高校教材、工具书等</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">40</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">余部（含合作），其中荣获国家、部委、省级优秀社会科学研究成果一、二、三等奖有</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">36</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">项。</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">1995</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">年获美国传记学会</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">&ldquo;</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">世界金纪录奖</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">&rdquo;</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">，</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">2005</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">年获海南省首届</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">&ldquo;</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">杰出人才</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">&rdquo;</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">称号。学术传记收入《中国经济学希望之光》、《海南百科全书》、《东方之子》等书。研究方向：中国现代经济理论、市场经济理论。</span><span lang=\"EN-US\" style=\"font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 16.3pt; margin: 0cm 0cm 0pt; word-break: break-all; mso-pagination: widow-orphan\"><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">研究工作：长期从事中国社会主义经济思想史研究工作，先后出版学术著作、高校教材、工具书等</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">40</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">余部（含合作），发表学术论文</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">260</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">多篇；其中有</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">35</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">项荣获国家、部委、省级优秀社会科学研究成果一、二、三等奖。</span><span lang=\"EN-US\" style=\"font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 16.3pt; margin: 0cm 0cm 0pt; word-break: break-all; mso-pagination: widow-orphan\"><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">主要论著</span><span lang=\"EN-US\" style=\"font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 16.3pt; margin: 0cm 0cm 0pt; word-break: break-all; mso-pagination: widow-orphan\"><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">　　</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">1.</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">主编：中国社会主义经济思想史简编；</span><span lang=\"EN-US\" style=\"font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 16.3pt; margin: 0cm 0cm 0pt; word-break: break-all; mso-pagination: widow-orphan\"><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">　　</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">2.</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">主编：中国社会主义经济思想研究；</span><span lang=\"EN-US\" style=\"font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 16.3pt; margin: 0cm 0cm 0pt; word-break: break-all; mso-pagination: widow-orphan\"><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">　　</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">3.</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">主编：中国现代经济理论研究；</span><span lang=\"EN-US\" style=\"font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 16.3pt; margin: 0cm 0cm 0pt; word-break: break-all; mso-pagination: widow-orphan\"><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">　　</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">4.</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">专著：中国社会主义经济思想史研究；</span><span lang=\"EN-US\" style=\"font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 16.3pt; margin: 0cm 0cm 0pt; word-break: break-all; mso-pagination: widow-orphan\"><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">　　</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">5.</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">专著：中国新时期经济理论发展研究；</span><span lang=\"EN-US\" style=\"font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 16.3pt; margin: 0cm 0cm 0pt; word-break: break-all; mso-pagination: widow-orphan\"><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">　　</span><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">6.</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">专著：中国社会主义经济思想导论；</span><span lang=\"EN-US\" style=\"font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; text-indent: 20.25pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span lang=\"EN-US\" style=\"font-family: Tahoma; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-font-kerning: 0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 7.</span><span style=\"font-family: 宋体; letter-spacing: 0.35pt; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">专著：中国现代经济理论探索；</span><span lang=\"EN-US\" style=\"font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; text-indent: 20.25pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span lang=\"EN-US\" style=\"font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt\">&nbsp;</span><span lang=\"EN-US\" style=\"font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; text-indent: 20.25pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span lang=\"EN-US\" style=\"font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt\">&nbsp;</span><span lang=\"EN-US\" style=\"font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; text-indent: 20.25pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span lang=\"EN-US\" style=\"font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt\">&nbsp;</span><span lang=\"EN-US\" style=\"font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; text-indent: 20.65pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><b style=\"mso-bidi-font-weight: normal\"><span style=\"font-family: 宋体; font-size: 14pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">孙仁佳</span></b><b style=\"mso-bidi-font-weight: normal\"><span lang=\"EN-US\" style=\"font-family: Tahoma; font-size: 14pt; mso-font-kerning: 0pt\">&nbsp;</span></b><span lang=\"EN-US\" style=\"font-family: Tahoma; font-size: 14pt; mso-font-kerning: 0pt\">&nbsp; <span style=\"mso-spacerun: yes\">&nbsp;</span></span><span style=\"font-family: 宋体; font-size: 14pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">教授</span><span lang=\"EN-US\" style=\"font-family: Tahoma; font-size: 14pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; text-indent: 20.65pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span style=\"font-family: 宋体; font-size: 14pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">现任三亚学院管理分院党总支书记</span><span lang=\"EN-US\" style=\"font-family: Tahoma; font-size: 14pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; text-indent: 20.65pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span lang=\"EN-US\" style=\"font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p>&nbsp;</o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 120%; text-indent: 21pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 12pt; mso-font-kerning: 0pt\">&nbsp;1945</span><span style=\"line-height: 120%; font-family: 宋体; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">年出生，山东龙口人。教授，硕士生导师。</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 12pt; mso-font-kerning: 0pt\">1970</span><span style=\"line-height: 120%; font-family: 宋体; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">年清华大学本科毕业，</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 12pt; mso-font-kerning: 0pt\">1982</span><span style=\"line-height: 120%; font-family: 宋体; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">年获大连理工大学硕士学位。长期从事金融投资方面教学和科研以及高校中层管理工作。</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 120%; text-indent: 21pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt\">&nbsp;</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 120%; text-indent: 21pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt\">&nbsp;</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 120%; text-indent: 21pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt\">&nbsp;</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 120%; text-indent: 20.5pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><b style=\"mso-bidi-font-weight: normal\"><span style=\"line-height: 120%; font-family: 宋体; color: black; font-size: 14pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">白秉三</span></b><span style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 14pt; mso-font-kerning: 0pt\"> <span lang=\"EN-US\"><span style=\"mso-spacerun: yes\">&nbsp;&nbsp;</span></span></span><span style=\"line-height: 120%; font-family: 宋体; color: black; font-size: 14pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">教授</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 14pt; mso-font-kerning: 0pt\">&nbsp;<span style=\"mso-spacerun: yes\">&nbsp; </span></span><span style=\"line-height: 120%; font-family: 宋体; color: black; font-size: 14pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">硕士生导师</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 14pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 120%; text-indent: 20.5pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span style=\"line-height: 120%; font-family: 宋体; font-size: 14pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">现任三亚学院管理分院<span style=\"color: black\">院长助理</span></span><span style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 12pt; mso-font-kerning: 0pt\"> </span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 120%; text-indent: 20.5pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 12pt; mso-font-kerning: 0pt\"><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 120%; text-indent: 6pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 12pt; mso-font-kerning: 0pt\">&nbsp;&nbsp;&nbsp; 1970&nbsp;</span><span style=\"line-height: 120%; font-family: 宋体; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">年毕业于北京大学，</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 12pt; mso-font-kerning: 0pt\"> 1981&nbsp;</span><span style=\"line-height: 120%; font-family: 宋体; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">年在北京科技大学（北京钢铁学院）获工学硕士学位，从</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 12pt; mso-font-kerning: 0pt\">&nbsp;1995&nbsp;</span><span style=\"line-height: 120%; font-family: 宋体; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">年开始担任硕士研究生导师。</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Arial; color: black; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 120%; text-indent: 24pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span style=\"line-height: 120%; font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">长期从事结构工程、断裂力学、工程力学领域的学术研究；出版《</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt\"><a title=\"进入网上书城检索书籍《断裂力学》\" target=\"_blank\" href=\"http://book.kaoyantj.com/kaoyanbook_search.asp?shuming=%B6%CF%C1%D1%C1%A6%D1%A7&amp;xuanze=2\"><span lang=\"EN-US\" style=\"font-family: 宋体; color: windowtext; text-decoration: none; text-underline: none; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma\"><span lang=\"EN-US\">断裂力学<span lang=\"EN-US\">》、《</span></span></span></a><a title=\"进入网上书城检索书籍《工程力学》\" target=\"_blank\" href=\"http://book.kaoyantj.com/kaoyanbook_search.asp?shuming=%B9%A4%B3%CC%C1%A6%D1%A7&amp;xuanze=2\"><span lang=\"EN-US\" style=\"font-family: 宋体; color: windowtext; text-decoration: none; text-underline: none; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma\"><span lang=\"EN-US\">工程力学<span lang=\"EN-US\">》、《</span></span></span></a><a title=\"进入网上书城检索书籍《结构损伤检测与智能诊断》\" target=\"_blank\" href=\"http://book.kaoyantj.com/kaoyanbook_search.asp?shuming=%BD%E1%B9%B9%CB%F0%C9%CB%BC%EC%B2%E2%D3%EB%D6%C7%C4%DC%D5%EF%B6%CF&amp;xuanze=2\"><span lang=\"EN-US\" style=\"font-family: 宋体; color: windowtext; text-decoration: none; text-underline: none; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma\"><span lang=\"EN-US\">结构损伤检测与智能诊断<span lang=\"EN-US\">》、</span></span></span><span style=\"color: windowtext; text-decoration: none; text-underline: none\">&nbsp;</span><span lang=\"EN-US\" style=\"font-family: 宋体; color: windowtext; text-decoration: none; text-underline: none; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma\"><span lang=\"EN-US\">《</span></span></a><a title=\"进入网上书城检索书籍《结构力学》\" target=\"_blank\" href=\"http://book.kaoyantj.com/kaoyanbook_search.asp?shuming=%BD%E1%B9%B9%C1%A6%D1%A7&amp;xuanze=2\"><span lang=\"EN-US\" style=\"font-family: 宋体; color: windowtext; text-decoration: none; text-underline: none; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma\"><span lang=\"EN-US\">结构力学<span lang=\"EN-US\">》等著作和教材；完成日本国文部省国际合作基金项目、</span></span></span><span style=\"color: windowtext; text-decoration: none; text-underline: none\">&nbsp;</span><span lang=\"EN-US\" style=\"font-family: 宋体; color: windowtext; text-decoration: none; text-underline: none; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma\"><span lang=\"EN-US\">国家自然科学基金项目和省部级科技项目多项，</span></span><span style=\"color: windowtext; text-decoration: none; text-underline: none\">2005.5&nbsp;</span><span lang=\"EN-US\" style=\"font-family: 宋体; color: windowtext; text-decoration: none; text-underline: none; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma\"><span lang=\"EN-US\">获沈阳市职工科技创新成果二等奖；</span></span></a></span><span style=\"line-height: 120%; font-family: 宋体; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">发表学术论文</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 12pt; mso-font-kerning: 0pt\">40</span><span style=\"line-height: 120%; font-family: 宋体; color: black; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">余篇。</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Arial; color: black; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 120%; text-indent: 24pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 12pt; mso-font-kerning: 0pt\">&nbsp;</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Arial; color: black; font-size: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 120%; text-indent: 24pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 12pt; mso-font-kerning: 0pt\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 120%; text-indent: 24pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\">&nbsp;</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 120%; text-indent: 21pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><b style=\"mso-bidi-font-weight: normal\"><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 14pt; mso-font-kerning: 0pt\">&nbsp;</span></b><b style=\"mso-bidi-font-weight: normal\"><span style=\"line-height: 120%; font-family: 宋体; color: black; font-size: 14pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">陈祥敏</span></b><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 14pt; mso-font-kerning: 0pt\"><span style=\"mso-spacerun: yes\">&nbsp; </span><span style=\"mso-spacerun: yes\">&nbsp;</span></span><span style=\"line-height: 120%; font-family: 宋体; font-size: 14pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">副教授</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; font-size: 14pt; mso-font-kerning: 0pt\"><span style=\"mso-spacerun: yes\">&nbsp; </span></span><span style=\"line-height: 120%; font-family: 宋体; font-size: 14pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">高级工程师</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 14pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 120%; text-indent: 20.5pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span style=\"line-height: 120%; font-family: 宋体; font-size: 14pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">&nbsp;现任三亚学院管理分院<span style=\"color: black\">院长助理</span></span><span style=\"line-height: 120%; font-family: Tahoma; color: black; font-size: 14pt; mso-font-kerning: 0pt\"> <span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 120%; text-indent: 21pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Arial; font-size: 9pt; mso-font-kerning: 0pt\"><o:p>&nbsp;</o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 120%; text-indent: 21pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt\">&nbsp;1967</span><span style=\"line-height: 120%; font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">年出生，四川广安人，副教授</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt\">/</span><span style=\"line-height: 120%; font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">高级工程师，</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt\">87</span><span style=\"line-height: 120%; font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">年毕业于中国石油大学，天津财经大学全日制</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt\">MBA</span><span style=\"line-height: 120%; font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">，曾在中国石油大学等单位任职多年，有多行业高管经验。现任海南大学三亚学院管理分院院长助理，主持学生工作等。曾出版著作</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt\">8</span><span style=\"line-height: 120%; font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">部，发表论文</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt\">15</span><span style=\"line-height: 120%; font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">篇，省市级项目多项，部局级荣誉多项。</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align: left; line-height: 120%; text-indent: 21pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"><span style=\"line-height: 120%; font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Tahoma; mso-hansi-font-family: Tahoma; mso-bidi-font-family: Tahoma; mso-font-kerning: 0pt\">现主要从事企业管理、人力资源等方面的研究工作，并担任经济管理类专业多门主干课程主讲。</span><span lang=\"EN-US\" style=\"line-height: 120%; font-family: Tahoma; font-size: 12pt; mso-font-kerning: 0pt; mso-bidi-font-size: 9.0pt\"><o:p></o:p></span></p>\r\n</span></span></div>\r\n<div style=\"line-height: 16.3pt; word-break: break-all\" align=\"left\"><span style=\"font-size: small\"><span style=\"font-family: Tahoma\"><span style=\"letter-spacing: 0.35pt; color: black\">　　</span></span></span></div>\r\n<div style=\"text-indent: 20.25pt\"><span style=\"font-size: small\"><span style=\"font-family: Tahoma\">&nbsp;</span></span></div>\r\n<div style=\"text-indent: 20.25pt\"><span style=\"font-size: small\"><span style=\"font-family: Tahoma\">&nbsp;</span></span></div>\r\n<div style=\"text-indent: 20.25pt\"><span style=\"font-size: small\"><span style=\"font-family: Tahoma\">&nbsp;</span></span></div>',129,''),(5,'<p style=\"text-align: center\"><img height=\"525\" alt=\"\" width=\"700\" src=\"/collega/upload/file/000001(1).jpg\" /></p>\r\n<p style=\"text-align: center\">&nbsp;</p>\r\n<p style=\"text-align: center\"><img height=\"525\" alt=\"\" width=\"700\" src=\"/collega/upload/file/00002(1).jpg\" /></p>\r\n<p style=\"text-align: center\">&nbsp;</p>\r\n<p style=\"text-align: left\">&nbsp;</p>\r\n<p style=\"text-align: left\">&nbsp;</p>\r\n<p style=\"text-align: left\">&nbsp;</p>\r\n<p style=\"text-align: left\">&nbsp;</p>\r\n<p style=\"text-align: left\">&nbsp;</p>\r\n<p style=\"text-align: left\">&nbsp;</p>',130,'title'),(6,'<p>&nbsp;</p>\r\n<div align=\"left\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 工商管理专业</b>（专业代码110201）</span></span></div>\r\n<div align=\"left\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </b></span></span></div>\r\n<div align=\"left\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 培养目标：</b>本专业培养系统掌握经济学、管理学基础理论和现代管理分析方法，熟悉工商企业运作原理，具有较高的外语水平，能在综合经济管理部门、企事业单位从事管理分析、预测、规划和实际管理工作，具有创新思维和实践能力的应用型、实用性中高级专门人才。</span></span></div>\r\n<div align=\"left\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </b></span></span></div>\r\n<div align=\"left\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 主要课程</b>：微观经济学、宏观经济学、会计学、统计学、经济法、管理学原理、市场营销学概论、现代企业理论、组织行为学、企业战略管理、人力资源管理、生产运营管理、ERP 、财务管理、现代物流管理等。</span></span></div>\r\n<div align=\"left\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;</b></span></span></div>\r\n<div align=\"left\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 学制</b>：四年</span></span></div>\r\n<div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;</b></span></span></div>\r\n<div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 授予学位</b>：管理学学士</span></span></div>\r\n<div align=\"left\">&nbsp;</div>\r\n<div align=\"left\">&nbsp;</div>\r\n<div align=\"left\">&nbsp;</div>\r\n<div>&nbsp;</div>',118,'title'),(7,'<p><span style=\"font-size: medium\"><b>\r\n<p>\r\n<p>&nbsp;</p>\r\n</p>\r\n</b></span><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b><b>\r\n<div align=\"left\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 工程管理专业</b>（专业代码<span>110104）</span></div>\r\n<div align=\"left\"><b>&nbsp;</b></div>\r\n<div align=\"left\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 培养目标：</b>本专业培养具备管理学、经济学、土木工程技术的基本知识，掌握现代管理科学的理论、方法、手段以及工程管理相关的专业技能，能在国内外工程建设领域从事项目决策和全过程管理，能在房地产开发、建设行政管理、物业管理及其他行政企事业单位从事工程项目管理、房地产开发经营、造价管理、物业管理和建设管理等工作，具有创新精神和实践能力的应用型复合高级专门人才。</div>\r\n<div align=\"left\"><b>&nbsp;</b></div>\r\n<div align=\"left\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 主要课程</b>：管理学原理、微观经济学、统计学、运筹学、会计学基础、财务管理、工程经济学、经济法、建设法规、工程项目管理、工程估价、工程招投标与合同管理、房地产开发与经营、工程项目融资、土木工程概论、工程力学、房屋建筑学、建筑结构</div>\r\n<div align=\"left\"><b>&nbsp;</b></div>\r\n<div align=\"left\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 学制：四年</b></div>\r\n<div align=\"left\"><b>&nbsp;</b></div>\r\n<div align=\"left\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 授予学位</b>：管理学学士</div>\r\n<div align=\"left\">&nbsp;</div>\r\n</b></b></span></span><b>\r\n<div align=\"left\">\r\n<div align=\"left\">&nbsp;</div>\r\n</div>\r\n</b></p>\r\n<div align=\"left\">&nbsp;</div>',119,'title'),(8,'<p>\r\n<div style=\"text-indent: 24.1pt; line-height: 150%; text-align: left\" align=\"left\"><b><span style=\"font-size: 12pt; line-height: 150%\">市场营销专业</span></b><span style=\"font-size: 12pt; line-height: 150%\">（专业代码110202）</span></div>\r\n<div style=\"text-indent: 24.1pt; line-height: 150%; text-align: left\" align=\"left\"><b><span style=\"font-size: 12pt; line-height: 150%\">&nbsp;</span></b></div>\r\n<div style=\"text-indent: 24.1pt; line-height: 150%; text-align: left\" align=\"left\"><b><span style=\"font-size: 12pt; line-height: 150%\">培养目标：</span></b><span style=\"font-size: 12pt; line-height: 150%\">本专业培养系统掌握市场营销的基础理论、基本知识和综合应用的基本技能，能在各类工商企业和事业单位从事市场营销工作，具有创新思维和实践能力的应用型、实用性高级专门人才。</span></div>\r\n<div style=\"text-indent: 24.1pt; line-height: 150%; text-align: left\" align=\"left\"><b><span style=\"font-size: 12pt; line-height: 150%\">&nbsp;</span></b></div>\r\n<div style=\"text-indent: 24.1pt; line-height: 150%; text-align: left\" align=\"left\"><b><span style=\"font-size: 12pt; line-height: 150%\">主要课程</span></b><span style=\"font-size: 12pt; line-height: 150%\">：管理学、微观经济学、宏观经济学、会计学、统计学、管理信息系统、财务管理、市场营销、经济法、消费者行为学、市场调查、国际市场营销、ERP、公共关系学</span></div>\r\n<div style=\"text-indent: 24.1pt; line-height: 150%; text-align: left\" align=\"left\"><b><span style=\"font-size: 12pt; line-height: 150%\">&nbsp;</span></b></div>\r\n<div style=\"text-indent: 24.1pt; line-height: 150%; text-align: left\" align=\"left\"><b><span style=\"font-size: 12pt; line-height: 150%\">学制</span></b><span style=\"font-size: 12pt; line-height: 150%\">：四年</span></div>\r\n<div style=\"text-indent: 24.1pt; line-height: 150%\"><b><span style=\"font-size: 12pt; line-height: 150%\">&nbsp;</span></b></div>\r\n<div style=\"text-indent: 24.1pt; line-height: 150%\"><b><span style=\"font-size: 12pt; line-height: 150%\">授予学位</span></b><span style=\"font-size: 12pt; line-height: 150%\">：管理学学士</span></div>\r\n<p>&nbsp;</p>\r\n<div style=\"text-indent: 24.1pt; line-height: 150%; text-align: left\" align=\"left\">&nbsp;</div>\r\n<p>&nbsp;</p>\r\n<div style=\"text-indent: 24.1pt; line-height: 150%; text-align: left\" align=\"left\">&nbsp;</div>\r\n</p>\r\n<p>&nbsp;</p>',120,'title'),(9,'<div align=\"left\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </b></span></span></div>\r\n<div align=\"left\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 信息管理与信息系统专业</b>（专业代码1101028）</span></span></div>\r\n<div align=\"left\">&nbsp;</div>\r\n<div align=\"left\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 培养目标：</b>本专业培养系统掌握信息管理、信息技术和系统分析与设计方法等方面的理论知识与技能，具备现代管理学、经济学理论基础，计算机科学与技术知识及应用能力，能在国家各级管理部门、工商企业、金融机构、行业、相关科研、教学等单位从事信息管理、信息系统分析、设计、实施管理和评价等方面工作，具有创新思维和实践能力的应用型、实用性高级专门人才。</span></span></div>\r\n<div align=\"left\">&nbsp;</div>\r\n<div align=\"left\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 主要课程</b>：管理信息系统、信息存储与检索、信息资源管理、数据结构、数据库原理与应用、面向对象程序设计、信息系统分析与设计、软件工程、管理学原理、西方经济学、生产运营管理、会计学基础、C语言、Java语言、电子商务与网络营销等</span></span></div>\r\n<div align=\"left\">&nbsp;</div>\r\n<div align=\"left\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 学制</b>：四年</span></span></div>\r\n<div align=\"left\">&nbsp;</div>\r\n<div align=\"left\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 授予学位</b>：管理学学士</span></span></div>\r\n<div align=\"left\">&nbsp;</div>',121,'title');

/*Table structure for table `links` */

