insert  into `links`(`name`,`webSite`,`picture_path`,`Links_id`) values ('三亚学院','http://www.syxyhn.com/','/collega/upload/picture/1346740723.jpg',9),('哈佛商学院','http://www.hbs.edu/','/collega/upload/picture/8556823892.jpg',10),('鹿特丹管理学院','http://www.rsm.nl/home','/collega/upload/picture/425842281.gif',11),('沃顿商学院','http://www.wharton.upenn.edu/','/collega/upload/picture/8641357563.jpg',12);

/*Table structure for table `news` */

DROP TABLE IF EXISTS `news`;

CREATE TABLE `news` (
  `news_id` int(11) NOT NULL AUTO_INCREMENT,
  `author_name` varchar(20) DEFAULT NULL,
  `category_id` int(11) DEFAULT NULL,
  `news_title` varchar(150) DEFAULT NULL,
  `news_author` varchar(50) DEFAULT NULL,
  `news_time` datetime DEFAULT NULL,
  `news_content` text,
  `news_saw_num` int(11) DEFAULT '0',
  `import` int(11) DEFAULT '0',
  `origin` varchar(20) DEFAULT '管理学院',
  `pic_path` varchar(80) DEFAULT NULL COMMENT '图片路径',
  `pic_desc` varchar(50) DEFAULT NULL COMMENT '图片描述',
  `is_pic` tinyint(1) DEFAULT '0' COMMENT '是否为图片新闻',
  PRIMARY KEY (`news_id`),
  KEY `FK_news_category` (`category_id`)
) ENGINE=MyISAM AUTO_INCREMENT=49 DEFAULT CHARSET=gbk;

/*Data for the table `news` */

insert  into `news`(`news_id`,`author_name`,`category_id`,`news_title`,`news_author`,`news_time`,`news_content`,`news_saw_num`,`import`,`origin`,`pic_path`,`pic_desc`,`is_pic`) values (12,NULL,133,'分院之星即将启幕',NULL,'2010-03-02 18:01:49','<p style=\"text-align: center\"><img height=\"160\" alt=\"\" width=\"120\" src=\"/collega/upload/file/鍒嗛櫌涔嬫槦(1).jpg\" /></p>\r\n<p>&nbsp;</p>',30,0,'管理学院','/collega/upload/star/9655796225.jpg','分院之星即将启幕',1),(10,'yangchun',14,'新学期，管理学子沐浴书涯阳光','46','2010-03-02 17:27:37','<div style=\"text-align: center\"><span style=\"font-size: small\"><img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/0301.jpg\" /></span></div><div style=\"text-align: center\">&nbsp;</div><div style=\"text-align: center\">&nbsp;</div><div style=\"text-align: center\">&nbsp;</div><div style=\"text-align: center\"><span style=\"font-size: small\"><img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/0302.jpg\" /></span></div><div style=\"text-align: center\">&nbsp;</div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 新学年开始了，寂静的校园再度欢腾起来。2010年3月1日，新学期的伊始，管理学子沐浴书涯阳光，回到到了明亮宽敞的教室，憧憬和践行着新学年的学习。</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 今天，朗朗的读书声见证了我们管理分院的新颜，师生勤奋的身影透视着我们进步的希望。新学年里，管理分院展现了良好的新气象、新开始，为管理分院明天拉开了美好的序幕，相信管理分院的明天将会更加美好。</span></span></div><div>&nbsp;</div><div>&nbsp;</div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; （记者部韩亚娟）</span></span></span></span></div><p>&nbsp;</p>',38,0,'管理分院',NULL,NULL,0),(9,'yangchun',24,'管理分院召开第一次全体教职工例会','46','2010-03-02 15:31:18','<div style=\"text-indent: 21pt\">&nbsp;</div><div style=\"text-indent: 21pt\">&nbsp;</div><div style=\"text-indent: 21pt; text-align: center\"><span style=\"font-size: xx-small\"><img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/1(3).jpg\" />&nbsp;&nbsp;&nbsp;&nbsp; </span></div><div style=\"text-indent: 21pt; text-align: center\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">管理分院召开建院以来第一次员工例会</span></span></div><div style=\"text-indent: 21pt; text-align: center\">&nbsp;</div><div style=\"text-indent: 21pt; text-align: center\"><span style=\"font-family: Tahoma\"><span style=\"font-size: x-small\"><img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/2.jpg\" /></span></span></div><div style=\"text-indent: 21pt; text-align: center\"><span style=\"font-size: medium\">&nbsp;分院院长王毅武教授&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 分院党总支书记孙仁佳教授&nbsp;</span></div><div style=\"text-indent: 21pt; text-align: center\"><span style=\"font-size: x-small\">&nbsp; </span></div><div style=\"text-indent: 21pt; text-align: center\"><span style=\"font-size: x-small\"><img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/3.jpg\" /></span></div><div style=\"text-indent: 21pt; text-align: center\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp; 分管教学工作院长助理白秉三教授&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;分管学生工作的院长助理陈祥敏副教授</span></div><div style=\"text-indent: 21pt; text-align: center\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;</span></span></div><div style=\"text-indent: 21pt\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp; 2月28日上午9：00，管理分院成立伊始，首次全体教职工例会在分院会议室召开，会议由分院党总支书记孙仁佳教授主持。</span></span></div><div style=\"text-indent: 15.75pt\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp; 首先王毅武院长发表了重要讲话，对本学年的工作作了全面的部署，要求分院做好专业、学科、课程、教材、团队五大建设，和&ldquo;三风建设&rdquo;&mdash;&mdash;学风、教风、考风。最后王院长围绕育人工作、专业评估、国际旅游岛建设等方面做了详细阐述。</span></span></div><div style=\"text-indent: 15.75pt\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp; 分管分院教学工作的白秉三院长助理和分管培训、学生工作的陈祥敏院长助理，对去年的工作做了重要总结，就本学年的各自分管工作做出布置。</span></span></div><div style=\"text-indent: 15.75pt\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp; 最后，孙书记针对科研工作、专业评估发表了重要讲话，。再次学习了学院的办学理念、价值和愿景，回顾三亚学院发展历程，展望国际旅游岛建设下的三亚学院，以及在三亚学院快速发展下的管理学院。号召全体教职工凝聚人心、增强信心，在新的一年里将管理分院以崭新的姿态树立于大家面前，塑造品牌，创造辉煌。</span></span></div><p>&nbsp;</p>',76,0,'管理分院',NULL,NULL,0),(8,'yangchun',24,'管理分院的网站于2010年3月3日凌晨零点试运行','46','2010-03-02 15:16:10','<p><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">热烈祝贺管理分院的网站于2010年3月3日凌晨零点试运行！经过分院的的紧张筹备，新成立的管理分院门户网站正式与全院师生见面。网站紧紧围绕学院的特点，力求简约，凸显特色。用三亚特有的自然和学院的人文气息，为广大师生提供一个便捷的交流桥梁。</span></span></p>',52,0,'管理分院',NULL,NULL,0),(11,'yangchun',14,'学业规划新学期，管理学子百态生活','46','2010-03-02 17:34:49','<div><span style=\"font-size: x-small\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">学业规划，是大学生通过解决学什么、怎么学、用什么学、什么时候学等问题，以确保自身顺利完成学业，为成功实现就业或开辟事业打好基础。在新学期之初，海南大学三亚学院管理分院的学生们纷纷自觉制定了自己的学业规划。</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&ldquo;这学期的时间较短，但是课程特别多。&rdquo;工商0903班的李佩佩同学说：&ldquo;我会努力提高自己的学习效率，争取在本学期通过计算机二级、英语四级和中文应用写作二级的考试。&rdquo;</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 相对于李佩佩的酷爱学习而言，市场营销0802班的张鹏同学更重视自己的业余爱好。&ldquo;我喜欢画画和篮球。我希望这学期能将自己一直构思的漫画画出来，同时也希望自己的篮球技术能更上一层楼。&rdquo;</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 信管0701班的赵聪同学则将本学期的重点由校内转向校外。&ldquo;我决定利用课余时间多做兼职，这样不仅可以帮助我将学到的理论知识转化为实践经验，而且还可以让我更多地接触社会，以便以后更好地走向社会。&rdquo;</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 即将毕业的工程管理0601班的孙玲同学先表达了自己对学校的留恋：&ldquo;快毕业了，还真有点舍不得。回想过去，我在这里度过了一段难忘的岁月。&rdquo;然后她坚定地说：&ldquo;我将带着对大学生活的回忆和对未来的憧憬，努力开辟出一片属于自己的天地。&rdquo;</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 规划是同学们前进的动力，同时也能增强同学们的自我约束力和自我管理的能力、希望同学们执行好自己制定的规划，做到&ldquo;不因虚度年华而悔恨，也不因碌碌无为而羞耻&rdquo;。</span></span></div><div>&nbsp;</div><div>&nbsp;</div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">（记者部韩亚娟）</span></span></span></span></div>',36,0,'管理分院',NULL,NULL,0),(18,'yangchun',14,'环境综合治理，我们在行动','46','2010-03-04 21:14:02','<div style=\"text-align: center\"><span style=\"font-size: small\"><img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/1(4).jpg\" /></span></div><div style=\"text-align: center\">&nbsp;<span style=\"font-family: Tahoma\"><span style=\"font-size: small\"><span style=\"font-size: small\">创建首善之区&nbsp;&nbsp; 管理学子阳光行动</span></span></span></div><div style=\"text-align: center\">&nbsp;</div><div style=\"text-align: center\"><span style=\"font-size: small\"><img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/2(1).jpg\" /></span></div><div style=\"text-align: center\"><span style=\"font-family: Tahoma\"><span style=\"font-size: small\">阳光社区&nbsp; 靠大家</span></span></div><div style=\"text-align: center\">&nbsp;</div><div style=\"text-align: center\"><span style=\"font-size: small\"><img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/003.jpg\" /></span></div><div style=\"text-align: center\"><span style=\"font-family: Tahoma\"><span style=\"font-size: small\">从寝室到教室&nbsp; 生活的温暖&mdash;&mdash;家</span></span></div><div style=\"text-align: center\">&nbsp;</div><div>&nbsp;</div><div><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;新的一年，我们渴望健康的生命，渴望环保的家园，渴望绿色，渴望遍地花开&hellip;&hellip;伟大事业需要伟大的创造。</span></span></div><div><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;管理分院为了贯彻&lt;&lt;国务院关于推进海南省国际旅游岛建设发展的若干意见&gt;&gt;,认真配合落实&quot;海南国际旅游岛建设环境综合整治年&quot;的活动安排,积极响应陆丹院长关于环境综合整治工作的方案，管理分院学生会干部走进了教室，学生宿舍。等地方进行卫生大扫除。干净无暇的地面,整齐有序的书桌.加上洁白透明的阳台门,在阳光的照射下,显得格外宽敞明亮.</span></span></div><div><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;通过此次行动，我们看到了管理分院的学生为构建阳光校园的支持，同时也反应出大家对创建国际旅游岛的热情。</span></span></div><div><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我们的行动仍将继续，关注环境，关注校园，我们一起加油!</span></span></div><div><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp; </span></span></div><div><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp; （管理分院学生会）</span></span></div><p>&nbsp;</p>',41,0,'管理分院',NULL,NULL,0),(19,'yangchun',24,'陆丹院长深情寄语，管理分院破冰扬帆远航','46','2010-03-04 15:37:41','<div align=\"center\"><input type=\"image\" height=\"450\" width=\"600\" src=\"http://guanli.syxyhn.com/collega/upload/file/030300.jpg\" /></div><div align=\"center\"><span style=\"font-size: small\">&nbsp;</span><span style=\"font-family: Tahoma\"><span style=\"font-size: small\">陆丹院长亲临管理分院指导工作</span></span></div><div align=\"center\">&nbsp;</div><div align=\"center\"><input type=\"image\" height=\"450\" width=\"600\" src=\"http://guanli.syxyhn.com/collega/upload/file/0303001.jpg\" /></div><div align=\"center\">&nbsp;<span style=\"font-size: small\"><span style=\"font-family: Tahoma\">认真听取指导的分院领导</span></span></div><div align=\"center\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 作为学院新成立的二级分院&mdash;&mdash;管理分院，在2010年新学期伊始，万象更新，管理分院的全体教职员工就以&ldquo;极力争取&rdquo;的精神，积极做好新学期的各项工作。2月28日下午六时整，为支持管理学院的发展，学院党委书记、执行院长陆丹教授，在百忙之中，亲临管理分院指导工作。</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陆丹院长分析了新成立的管理分院所面临的机遇和挑战，对分院新一代的领导班子和教职员工寄予厚望。鼓励管理分院认真肯定原经管分院的同时，及时总结经验，因条件设计制度；鼓励分院课程改革，积极对外拓展，积极发出管理分院的声音，抓住国际旅游岛建设的机遇，为学院的发展做更多更出色的贡献；鼓励大家多动脑筋，要有志气和勇气。</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 陆丹院长说：&ldquo;源源生生不息的学生，昙花一现的教授，我们要把学术价值、思想、好作风给三亚学院留下来，培育出好学的学生。尊师重教，爱护学生&rdquo;。勉励管理分院，迎难而上，创造辉煌。</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 陆丹院长还就管理分院的工作作出了细致的布置。</span></span></div><p>&nbsp;</p>',101,0,'管理分院',NULL,NULL,0),(24,'yangchun',100,'【学子创业】初生牛犊不怕虎 工商雏鹰崭露头角 ----工商管理学生宋非纪实','46','2010-03-05 16:34:14','<p style=\"text-align: center\"><img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/101.jpg\" /></p><p style=\"text-align: center\"><span style=\"font-size: small\"><span style=\"font-family: Tahoma\">06级工商管理（企业管理方向）的宋非同学和他创立的餐厅</span></span></p><p style=\"text-align: center\"><img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/103.jpg\" /></p><p style=\"text-align: center\"><span style=\"font-size: small\"><span style=\"font-family: Tahoma\">宋非与同事</span></span></p><p style=\"text-align: center\"><img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/102.jpg\" /></p><p style=\"text-align: center\"><span style=\"font-family: Tahoma\"><span style=\"font-size: small\">餐厅一瞥</span></span></p><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2009年5月份，工商管理（企业管理方向）的宋非同学，已经开始了自己在家乡河北秦皇岛的创业征程，将在学院积累的知识和经验成功运用到了自己的创业工程中，9月，由宋非同学亲自策划的品牌西餐厅----艾品牛排开始运营。</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 宋非担任餐厅经理，主要工作：企业文化建设、企业战略规划，餐厅风格设计，日常管理。主营业务以牛排、咖啡为主。定位是高品质享受，中低价位的时尚牛排西餐厅。目标群体为18至30岁年轻群体。</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 价值观</b>：快乐至上，以人为本，追求卓越。</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 愿景</b>：成为一个引领人们生活方式，肩负社会责任感的长寿企业。</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 使命</b>：为顾客提供健康、营养的食品和高品质的生活体验。</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;今日我以三亚学院为荣，明日三亚学院以我为傲&rdquo;</span></span></div><p><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我们衷心祝愿我们三亚学院培养的阳光大学生创业路上一帆风顺，勇攀心目中的第一高峰。</span></span></p><p>&nbsp;</p><p>&nbsp;</p><p><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp; </span></span></p>',57,0,'管理分院',NULL,NULL,0),(25,'yangchun',5,'关于计算机二级考试辅导班开课的通知','46','2010-03-07 16:56:00','<div style=\"text-align: left\">&nbsp;</div><div style=\"text-align: left\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">管理分院各班：</span></span></div><div style=\"text-align: left\">&nbsp;</div><div style=\"text-align: left\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 根据学院安排，将于3月6日开始计算机二级辅导培训，请报名参加辅导培训的同学，按照《二级辅导班开课课表》和课程要求。准时参加。</span></span></div><div style=\"text-align: left\">&nbsp;</div><div style=\"text-align: left\">&nbsp;</div><div style=\"text-align: left\">&nbsp;</div><div style=\"text-align: right\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 管理分院</span></span></div><div style=\"text-align: right\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2010年3月</span></span></div><div style=\"text-align: left\">&nbsp;</div><div style=\"text-align: left\">&nbsp;</div><div style=\"text-align: left\">&nbsp;</div><div style=\"text-align: left\">&nbsp;</div><div style=\"text-align: left\">&nbsp;</div><div style=\"text-align: left\">&nbsp;</div><div align=\"center\">&nbsp;</div><p>&nbsp;</p>',65,0,'管理分院',NULL,NULL,0),(26,'yangchun',24,'管理学子 放彩吉利总部','46','2010-03-08 20:19:27','<div style=\"text-align: center\"><span style=\"font-size: small\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/001.jpg\" /></span></span></div><div style=\"text-align: center\"><span style=\"font-size: small\"><span style=\"font-family: Tahoma\">就职于浙江吉利控股集团人力资源部的</span></span></div><div style=\"text-align: center\"><span style=\"font-size: small\"><span style=\"font-family: Tahoma\">王民权（工商管理06级毕业生）在现场招聘</span></span></div><div>&nbsp;</div><div style=\"text-align: center\"><span style=\"font-size: small\"><span style=\"font-family: Tahoma\"><img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/鐜嬫皯鎋冧富鎸_錩夊埄闆嗗洟鏂版槬鑏旀?鏅氫細.jpg\" /></span></span></div><div style=\"text-align: center\"><span style=\"font-size: small\"><span style=\"font-family: Tahoma\">王民权和同事一道主持吉利集团新春联欢晚会</span></span></div><div style=\"text-align: center\">&nbsp;</div><div style=\"text-align: center\"><span style=\"font-size: small\"><span style=\"font-family: Tahoma\"><img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/鐜嬫皯鎋冧笌錩屼簨浠?細椁_.jpg\" /></span></span></div><div style=\"text-align: center\"><span style=\"font-size: small\"><span style=\"font-family: Tahoma\">王民权（中）与同事们快乐地生活在一起</span></span></div><div style=\"text-align: left\"><span style=\"font-size: small\"><span style=\"font-family: Tahoma\"><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 王民权，海南大学三亚学院工商管理专业06级学生。现就业于浙江吉利控股集团人力资源部，主要负责招聘模块和员工关系管理的事宜。他和千千万万个大学生一样，一毕业，怀揣着梦想离开校园，踏上寻找实现自己美好愿景的路途。在吉利集团2010校园招聘会上被吉利集团录用。他像个懵懂的孩子一样来到了集团，尽管他在学校曾是经管分院学生会主席，尽管他在学校时参加过很多的活动，得到过很多的锻炼，可真正的到了工作岗位上他才明白，自己需要学习的东西还有很多很多。还好他的适应能力比较强，很快便融入了这个大的团队当中。来到了集团，感受到了集团的温暖，了解到了整个集团的运作，他才真正的感受到吉利集团是多么伟大的一个企业，是多么值得世人尊敬的一个企业，它的成就是值得任何一个在吉利工作的人和任何一个心存梦想的人分享的。而我们三亚学院更是吉利教育产业的中坚力量，正是因为三亚学院有了今天如此大的发展，他才会有机会来到集团。</span></span></div><div><span style=\"font-size: small\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;在集团总部，他每天都是兢兢业业、勤勤恳恳的工作，抱着求学的心态，虚心向周围的同事们学习。因为他知道，他需要学习的东西有太多太多，他一定要尽快的成长起来，适应这个大环境。吉利集团刚刚举办了2010年的新年晚会，他很有幸和一名女同事担当主持人并表演了节目。<b>当集团杨健总裁得知他是三亚学院的学生时，杨总高兴的说：&ldquo;我们三亚学院的学生还是很优秀的嘛！&rdquo;</b> 他虽然很幸运的来到了集团，但是在他的努力之下，他可以很自豪的说，他没有为母校丢脸，他同样让周围的人都感觉到了三亚学院的学生是多么的优秀。</span></span></div><div><span style=\"font-size: small\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;衷心祝愿并祝福我们的学生能够更好的与社会接洽，真正践行&ldquo;走进校园的目的是为了更好的走上社会&rdquo;的理念。&nbsp;</span></span></div><div><span style=\"font-size: small\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;我们能做到，而且一定能做到，因为我们就是奇迹。</span></span></div><div>&nbsp;</div><div>&nbsp;</div><div>&nbsp;</div><div><span style=\"font-size: small\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; （管理分院学生工作办公室）</span></span></div><div>&nbsp;</div><div>&nbsp;</div>',113,0,'管理分院学生办公室',NULL,NULL,0),(31,'admin',130,'管理分院组织机构图','21','2010-03-10 10:15:34','<p style=\"text-align: center\"><img height=\"525\" alt=\"\" width=\"700\" src=\"/collega/upload/file/000001.jpg\" /></p>\r\n<p style=\"text-align: center\">&nbsp;</p>\r\n<p style=\"text-align: center\"><img height=\"525\" alt=\"\" width=\"700\" src=\"/collega/upload/file/00002.jpg\" /></p>',0,0,'',NULL,NULL,0),(28,'yangchun',14,'打造首善之区  共建文明宿舍','46','2010-03-08 21:53:08','<div>&nbsp;&nbsp;&nbsp;<span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;为了响应学院三月环境综合治理的号召，管理分院积极动员同学们行动起来，保持宿舍卫生，营造一个良好学习生活环境。同时为迎接3月10日的全校宿舍卫生大检查。管理分院团委学生会于3月7日下午对本院学生宿舍进行了一次全面卫生检查。</span></span></div><div><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 下午1点30分，管理分院团委学生会全体成员准时在北四篮球场集合。学生会干部们细心地为大家分好组，并详细地讲解了检查的要点。</span></span></div><div><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 为了不打搅同学们中午休息，检查工作定于下午三点准时开始。分好组的干事们在各部门部长的带领下分别到指定宿舍楼进行检查工作。本着认真负责，礼貌待人工作态度进行检查登记，得到同学们的积极配合。</span></span></div><div><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">在团委学生会各位工作人员努力、各位同学积极配合下，卫生检查工作顺利完成。这次检查不仅对整个管理分院是一次考验，使其更加紧密的团结。更是对全分院宿舍卫生、文明建设的一次最好的动员！让我们一起为打造一个卫生文明的管理分院而努力。</span></span></div><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 管理分院记者部 </span></span></p>',45,0,'管理分院学生会',NULL,NULL,0),(29,'yangchun',24,'展学生党员鲜明旗帜  续环境整治欣乐篇章','46','2010-03-09 17:51:19','<div style=\"text-align: center\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/025.jpg\" /></span></span></div><div style=\"text-align: center\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">管理分院团委书记彭伟伟老师在学生党员动员会现场作主题发言</span></span></div><div style=\"text-align: center\">&nbsp;</div><div style=\"text-align: center\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/026.jpg\" /></span></span></div><div style=\"text-align: center\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">管理分院学生党支部动员会现场</span></span></span></span></div><div style=\"text-align: center\">&nbsp;</div><div style=\"text-align: center\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/022.jpg\" /></span></span></div><div style=\"text-align: center\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">以身作则&nbsp;&nbsp; 践行模范&mdash;&mdash;&ldquo;党员寝室&rdquo;</span></span></div><div style=\"text-align: center\">&nbsp;</div><div style=\"text-align: center\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/023.jpg\" /></span></span></div><div style=\"text-align: center\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">分院学生党员和他们的寝室</span></span></div><div style=\"text-align: center\">&nbsp;</div><div style=\"text-align: center\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\"><img height=\"450\" alt=\"\" width=\"600\" src=\"/collega/upload/file/024.jpg\" /></span></span></div><div style=\"text-align: center\"><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">学生党员在寝室作现场交流</span></span></div><div style=\"text-align: center\">&nbsp;</div><div style=\"text-align: center\">&nbsp;</div><div style=\"text-align: center\">&nbsp;</div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 阳光地带建设阳光学府，阳光学府培养阳光学子，海南大学三亚学院管理分院一直把大学生党建工作作为一项重要任务常抓不懈，积极探索大学生党员先进性建设的新思路，为了响应建设海南国际旅游岛的建设，也为了配合三月学院环境卫生大检查的工作，管理分院学生党支部积极行动起来，带领所有党员同志，积极开展一系列活动。</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2010年3月9日中午12点，管理分院团委书记彭伟伟老师，巴扎汉老师、杨春老师及分院所有学生党员、预备党员同志参加了学生党支部的动员会，会议强调了学生党员在工作中、学习中、思想上要具有积极性和先锋模范作用，要以实际行动展现大学生的精神风貌。</span></span></div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 学生党支部为了让党员同志作好模范带头作用，在9日下午，每位党员宿舍门口帖上了党员寝室的红色标志牌，十分醒目。即鼓励党员同志作好卫生标兵工作，同时也号召同学们积极学习。这次活动让学生党员自觉的以身作则，以自身为标杆。增强了支部对党员的凝聚力，完善了对学生党员的监督与考核，提高了党员自我管理水平的发挥和党员自我教育作用，同时也是在响应建设海南国际旅游岛方面增强学生党员服务社会的能力，以此达到我们管理分院的此次工作目标&mdash;&mdash;建设国际旅游岛，争做文明大学生，从我做起，从你做起！</span></span></div><div>&nbsp;</div><div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; （管理分院学生党支部）</span></span></div><div>&nbsp;</div><div>&nbsp;</div><div>&nbsp;</div><div>&nbsp;</div><div>&nbsp;</div><div>&nbsp;</div><div>&nbsp;</div>',86,0,'管理分院学生党支部',NULL,NULL,0),(30,'admin',128,'管理分院简介','21','2010-03-10 09:46:21','<div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 海南大学三亚学院管理分院成立于2010年1月，是由原经济管理分院拆分而成立的。分院现设有市场营销、工商管理、工程管理、信息管理与信息系统和人力资源管理五个本科专业，目前管理分院共有在校本科生2000余人。</span></span></div>\r\n<div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;</span></span></div>\r\n<div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 管理分院是一个具有丰富经验和活力的办学团队，现有教职工60余人。专职教师中具有教授、副教授职称的教师占29%，讲师和博士占38%，其余为具有硕士研究生学历的青年教师。分院目前已经形成了一支学历较高、职称年龄结构相对合理、具有较好的能力和素质的师资队伍。分院坚持学院的办学图景、理念和价值，坚持以育人为根本任务，以教学为中心，以科研为基础，不断提高教学质量，培养掌握一定专业知识和技能，具有创新精神、实践能力和健康人格的应用型人才。</span></span></div>\r\n<div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 在教学方面，分院提倡大胆开展教学改革，不断创新教学理念和方法；提倡以情景教学、案例教学为主的互动式教学方式；更新教学观念，改革教学内容、教学形式、教学方法手段，加强实践性教学，受到学生普遍欢迎。在科研上，已发表多篇论文和著作，获得省社科联、教育厅和学校多个科研项目,有的论文被海南省社科联评为一等奖。</span></span></div>\r\n<div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp; </span></span></div>\r\n<div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 管理分院紧抓高等教育大众化的契机和社会转型时期细分的市场对于人才的需求，进行人才培养模式的革新，使人才培养既符合市场具有&ldquo;上岗&rdquo;竞争力的&ldquo;工具性导向&rdquo;实用性人才的需求，又符合人的全面、可持续、&ldquo;上梯阶&rdquo;发展的&ldquo;超功利导向&rdquo;的健康人格指向。管理分院进行教学计划修订和课程改革，强化学生对外语、计算机、中文应用写作三门交往工具的掌握；贯彻&ldquo;三级实习&rdquo;，建立不同时段、不同层次的实训和毕业前的就业再培训机制，培养学生&ldquo;用得着、带得走、接得上&rdquo;的专业知识和专业技能；强化公共政治课程、班级文化建设、宿舍文化建设，打通校内外实践；促使学生了解国情、懂得趋势、习惯礼仪、能够沟通，引导学生积累社会资本；致力于把学生培养成具有创新精神、学习能力和实践能力的应用型、实用性高级专门人才。 </span></span></div>\r\n<div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;</span></span></div>\r\n<div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 管理学院为增强学生动手，培养实践技能打造良好平台。现有一个管理综合实验室、多个校外实习基地。管理分院学生为丰富业余学习生活积极开展第二课堂活动，先后获得了多个奖项。这些为学生健康的体魄和健康的人格培养提供了优良的土壤。</span></span></div>\r\n<div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;</span></span></div>\r\n<div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 面对新的发展形势，管理分院紧紧围绕 &ldquo;走进校园是为了更好地走向社会&rdquo; 这一崭新的育人理念，秉承&ldquo;自立更生，合力更新，有容乃大，不懈则优&rdquo;的校训和&ldquo;豪气，大气，正气&rdquo;的院风，按照311培养的培养模式，我院将加强学科建设和师资队伍的建设，强化规范管理、优质服务，打造学生在德、智、体方面全面发展的良好平台，培养出建设祖国的栋梁人才。</span></span></div>\r\n<div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp; </span></span></div>\r\n<div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 教育改变命运，大学提供阶梯。成千上万的年轻学子走进三亚学院管理分院，会在全新成长理念的熏陶下，焕发自身的学习兴趣和热情，养成适应社会进步和先进文化发展的高品质，真正地愉快学习、自主成长，更好地走上社会。</span></span></div>\r\n<div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;</span></span></div>\r\n<div><span style=\"font-family: Tahoma\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 三亚学院管理分院</span></span></div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>',0,0,'',NULL,NULL,0),(48,'yangchun',14,'在新起点上——工商管理专业2008级2010新学期主题班会','46','2010-03-12 15:42:54','<div _extended=\"true\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\"><span _extended=\"true\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3</span><span _extended=\"true\">月</span><span _extended=\"true\">11</span><span _extended=\"true\">日</span><span _extended=\"true\">下午，管理分院工商管理专业2008级举行了</span><span _extended=\"true\">2010</span><span _extended=\"true\">新学期班会，会议由工商管理专业08级辅导员谢敏慧老师主持，工商管理专业08级全体同学参加了此次班会。</span></span></span></div><div _extended=\"true\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\"><span _extended=\"true\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 当大家还沉浸在欢度春节的喜悦中时，新学期悄然到来。在过去的一年里，工商管理08级的近两百名同学，在学院各级领导的关心和呵护下，在老师们的指导和教育下，齐心协力，共同携手，取得了骄人的成绩，得到了广泛的认可，成为管理分院中的一朵奇葩。新的学年，同学们将继往开来，争取更大的进步。</span></span></span></div><div _extended=\"true\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\"><span _extended=\"true\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span _extended=\"true\">会上，谢敏慧老师总结了过去，为大家取得的成绩感到骄傲。同时也介绍了新建管理分院的基本情况，鼓励大家在新形势下再接再厉，为管理分院的建设贡献自己的力量。同时还对开学以来的报到、出勤、卫生、文明、学习等问题进行了说明。号召大家在海南建设国际旅游岛的大环境下，着力提高自身素质，加强文化学习，注重生活环境，增强道德涵养。</span></span></span></div><div _extended=\"true\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\"><span _extended=\"true\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span _extended=\"true\">会后，大家讨论热烈，同学们纷纷表示，在新的学期，一定不辜负老师的期望，积极响应学院号召，愉快学习，自主成长，做一名朝气蓬勃、积极向上的阳光学子。</span></span></span></div><div _extended=\"true\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;</span></span></div><div _extended=\"true\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\"><span _extended=\"true\">&nbsp;&nbsp; （</span><span _extended=\"true\">撰稿</span><span _extended=\"true\">&nbsp;</span><span _extended=\"true\">陈明&nbsp;&nbsp; </span><span _extended=\"true\">王莉）</span></span></span></div>',32,0,'工商管理专业08级',NULL,NULL,0),(33,'yangchun',100,'【就业政策】高校毕业生将可获至少10天创业培训','46','2010-03-10 21:52:57','<div align=\"left\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></div><div align=\"left\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 今年我国登记求职的高校毕业生，可获得中短期(1－6个月)实用技能培训和不少于10天的创业培训。昨天，人力资源和社会保障部、国家发改委、财政部公布了《关于进一步实施特别职业培训计划的通知》。</span></span></div><div align=\"left\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　通知透露，今年我国将全面推进创业培训。重点对有创业愿望并具备一定创业条件的登记失业人员、进城求职农村劳动者及返乡农民工、登记求职的高校毕业生，开展不少于10天的创业培训。要结合当地产业发展和创业项目，根据不同对象的特点，因材施教，增强创业培训的实效性，并注重创业培训与小额担保贷款、税费减免等扶持政策的衔接，着力提高创业成功率和稳定经营率。</span></span></div><div align=\"left\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　此外，各地还要积极开展中短期实用技能培训。对登记失业人员、进城求职农村劳动者、登记求职的高校毕业生，要结合企业的实际需要和劳动者就业要求，积极开展中短期(1－6个月)实用技能培训。各地人力资源社会保障部门要结合农村转移劳动者向城市非农产业转移和有组织的劳务输出需求，结合失业人员和高校毕业生特点以及就业需求，充分发挥基层劳动保障工作平台的组织作用，依托定点培训机构开展订单式培训，突出培训的针对性和有效性。</span></span></div><div align=\"left\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　对未能继续升学的农村初高中毕业生(即&ldquo;两后生&rdquo;)，各地需鼓励其参加6－12个月的劳动预备制培训，提升技能水平和就业能力。</span></span></div><div align=\"left\">&nbsp;</div><div align=\"left\">&nbsp;</div><div align=\"left\">&nbsp;</div><div align=\"left\">&nbsp;</div><div align=\"left\">&nbsp;</div><div align=\"left\">&nbsp;</div><div align=\"left\">&nbsp;</div>',8,0,'管理分院',NULL,NULL,0),(36,'yangchun',100,'【就业政策】大学生与劳动法','46','2010-03-10 22:25:43','<div class=\"mainNewsContent NewsContent\" id=\"mainNewsContent\"><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 又到了临近毕业的大学生陆续奔波各种招聘会的的季节,不论是用人单位录用新员工，还是大学毕业生等其他求职者成功进入了新单位，都不可避免的会涉及到劳动合同的签订问题。如何签订以及如何签好劳动合同，为以后劳动争议的发生减少隐患。重要的就是你能否运用法律的武器来保护好自己。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　劳动关系的存在一般来说是以劳动合同为标志。因此，签订劳动合同、签好劳动合同，将有利于劳动争议的预防。而要签好劳动合同，就需要熟悉《劳动法》等法律，清楚劳动者和用人单位的权责。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　签订劳动合同首先得在形式上完备。劳动合同是双方在自愿基础上平等协商后，经过双方签字生效的。《劳动法》第十九条就规定劳动合同应当以书面形式订立。对于大学应届毕业生来说，他们首先签订的是就业协议书。这每人一份的就业协议书是学校、学生和用人单位三方共同签署的。就业协议是三方的一种合意，是签订劳动合同前特殊阶段的一种特殊协议，也具有劳动合同的相似性质。大学应届毕业生与用人单位签订了就业协议后，便受到该协议的法律约束，不能够擅自毁约。就业协议书的签订在一定程度上限制了学生的再次选择权，学生应该注意这点。而学生进入单位后还是要同用人单位签订劳动合同的，因为劳动合同中规定的内容远比就业协议宽泛具体。但是如果用人单位在学生进入单位进行工作后还不签订劳动合同，那么就业协议将起到劳动合同的作用。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　另外，在现实社会生活中，经常存在用人单位不与劳动者签订劳动合同或者拖延签订劳动合同等现象。首先，劳动者应清楚知道用人单位的这种做法是不合法的，应尽量争取签订劳动合同，以维护自己的合法权利。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　其次，劳动者还应该注意劳动合同的具体内容。因为这部分主要规定了双方的权利义务等诸多的事项，内容的不完善很容易引发劳动争议。《劳动法》第十九条规定了劳动合同中应该具备的条款：合同的期限；工作内容；劳动保护与劳动条件；劳动报酬；劳动纪律；劳动合同终止的条件；违反劳动合同的责任。劳动合同的内容不能够违反法律行政法规的相关规定，这是强制性规定。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第三，劳动者还应该着重注意试用期是双方相互了解、相互选择的特殊时期，也应该签订劳动合同，在试用期间劳动者还依法享有社会保险待遇。有的用人单位经常借试用期之由不签订劳动合同，这是不合法的。在劳动合同中可以约定试用期限，但不允许先试用后签订劳动合同，而且在此期间，劳动者可以随时解除合同，只要提前通知即可；而用人单位虽也可如此，但必须提供足够的证据证明劳动者不适合该职位。一般来说试用期最长不得超过六个月。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第四，劳动者还应当事先问清用人单位工资具体构成，比如基本工资，津贴，奖金等，在合同中要着重写明工资标准，因为有的单位发给劳动者的实际工资可能与承诺的不一样。另外关于法定社会保险，一般来说正规的用人单位都会给劳动者购买医疗保险、养老保险、工伤保险和失业保险等四种社会保险的。但也有的却为片面追求自己的利益而不给劳动者购买，这是也违法的，劳动者因为生计原因可暂时不要，但必须知道这是自己应该享有的权利。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　最后，劳动者还应该对合同解除的条件以及违约责任等内容予以详细具体约定，对于应届毕业生来说，尤其应该注意。因为毕业生总是在求职的时候存在不满足的心态，遇到更好的就会把前面的单位给抛弃，因此用人单位一般都会在就业协议中约定很高的违约金以限制毕业生的毁约。所以大学毕业生不可轻易的违约，即使要解约，也该提前通知，以防劳动争议的发生，产生赔偿责任。因为一旦不履行提前通知的义务，单位有可能通过法律途径要求学生赔偿单位的招聘费用。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　对于属于自己依法享有的权利，劳动者应该积极地予以争取。对于用人单位的无理要求，劳动者也要据理力争，坚决地予以反对，坚决地维护好自己的合法权益。</span></span></p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p></div>',8,0,'管理分院',NULL,NULL,0),(35,'yangchun',100,'北京汇源果汁海南办事处人员招聘信息','46','2010-03-10 22:09:59','<div style=\"line-height: 23pt\"><b><span style=\"font-size: 12pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 公司简介：</span></b><span style=\"font-size: 12pt\">北京汇源果汁以&ldquo;一流的企业，一流的管理，一流的产品&rdquo;为经营理念，以&ldquo;团结、拼博、开拓、创新&rdquo;为企业精神，以&ldquo;高起点、高效率、高质量&rdquo;为目标，诚聘行业精才加盟一起开拓&ldquo;汇源&rdquo;美好明天！</span></div><div style=\"line-height: 23pt\"><b><span style=\"font-size: 12pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 招聘岗位：</span></b><span style=\"font-size: 12pt\">销售代表：3名</span></div><div style=\"line-height: 23pt\"><b><span style=\"font-size: 12pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 职位要求：</span></b></div><div style=\"line-height: 23pt\"><span style=\"font-size: 12pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1</span><span style=\"font-size: 12pt\">、大专以上学历，性别不限， 能吃苦耐劳。</span></div><div style=\"line-height: 23pt\"><span style=\"font-size: 12pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2</span><span style=\"font-size: 12pt\">、有志在快速消费品行业发展，有亲和力善于与人沟通、执行力强、具有服务意识、积极完成预定目标。</span></div><div style=\"line-height: 23pt\"><b><span style=\"font-size: 12pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 薪酬待遇：</span></b></div><div style=\"margin-left: 36pt; text-indent: -36pt; line-height: 23pt\"><span style=\"font-size: 12pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 工资=岗位工资+绩效工资+销岗位补助+销售提成（1800&mdash;2500元/月）；</span></div><div style=\"margin-left: 36pt; text-indent: -36pt; line-height: 23pt\"><span style=\"font-size: 12pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 公司购买五险,享受各种福利待遇。</span></div><div style=\"line-height: 23pt\"><b>&nbsp;</b></div><div style=\"line-height: 23pt\"><b><span style=\"font-size: 12pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 联系人：</span></b><span style=\"font-size: 12pt\">周先生</span></div><div style=\"line-height: 23pt\"><b><span style=\"font-size: 12pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 电&nbsp;话：</span></b><span style=\"font-size: 12pt\">18976093986</span></div><div style=\"line-height: 23pt\">&nbsp;</div><div style=\"line-height: 23pt\">&nbsp;</div><div style=\"line-height: 23pt\">&nbsp;</div><div style=\"line-height: 23pt\">&nbsp;</div><div style=\"line-height: 23pt\">&nbsp;</div>',9,0,'管理分院',NULL,NULL,0),(37,'yangchun',100,'【就业政策】劳动合同法实施条例全文','46','2010-03-10 22:28:18','<p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\"><strong>第一章　总则</strong></span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第一条　为了贯彻实施《中华人民共和国劳动合同法》(以下简称劳动合同法)，制定本条例。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第二条　各级人民政府和县级以上人民政府劳动行政等有关部门以及工会等组织，应当采取措施，推动劳动合同法的贯彻实施，促进劳动关系的和谐。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第三条　依法成立的会计师事务所、律师事务所等合伙组织和基金会，属于劳动合同法规定的用人单位。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\"><strong>　　第二章　劳动合同的订立</strong></span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第四条　劳动合同法规定的用人单位设立的分支机构，依法取得营业执照或者登记证书的，可以作为用人单位与劳动者订立劳动合同；未依法取得营业执照或者登记证书的，受用人单位委托可以与劳动者订立劳动合同。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第五条　自用工之日起一个月内，经用人单位书面通知后，劳动者不与用人单位订立书面劳动合同的，用人单位应当书面通知劳动者终止劳动关系，无需向劳动者支付经济补偿，但是应当依法向劳动者支付其实际工作时间的劳动报酬。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第六条　用人单位自用工之日起超过一个月不满一年未与劳动者订立书面劳动合同的，应当依照劳动合同法第八十二条的规定向劳动者每月支付两倍的工资，并与劳动者补订书面劳动合同；劳动者不与用人单位订立书面劳动合同的，用人单位应当书面通知劳动者终止劳动关系，并依照劳动合同法第四十七条的规定支付经济补偿。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　前款规定的用人单位向劳动者每月支付两倍工资的起算时间为用工之日起满一个月的次日，截止时间为补订书面劳动合同的前一日。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第七条　用人单位自用工之日起满一年未与劳动者订立书面劳动合同的，自用工之日起满一个月的次日至满一年的前一日应当依照劳动合同法第八十二条的规定向劳动者每月支付两倍的工资，并视为自用工之日起满一年的当日已经与劳动者订立无固定期限劳动合同，应当立即与劳动者补订书面劳动合同。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第八条　劳动合同法第七条规定的职工名册，应当包括劳动者姓名、性别、公民身份号码、户籍地址及现住址、联系方式、用工形式、用工起始时间、劳动合同期限等内容。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第九条　劳动合同法第十四条第二款规定的连续工作满10年的起始时间，应当自用人单位用工之日起计算，包括劳动合同法施行前的工作年限。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第十条　劳动者非因本人原因从原用人单位被安排到新用人单位工作的，劳动者在原用人单位的工作年限合并计算为新用人单位的工作年限。原用人单位已经向劳动者支付经济补偿的，新用人单位在依法解除、终止劳动合同计算支付经济补偿的工作年限时，不再计算劳动者在原用人单位的工作年限。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第十一条　除劳动者与用人单位协商一致的情形外，劳动者依照劳动合同法第十四条第二款的规定，提出订立无固定期限劳动合同的，用人单位应当与其订立无固定期限劳动合同。对劳动合同的内容，双方应当按照合法、公平、平等自愿、协商一致、诚实信用的原则协商确定；对协商不一致的内容，依照劳动合同法第十八条的规定执行。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第十二条　地方各级人民政府及县级以上地方人民政府有关部门为安置就业困难人员提供的给予岗位补贴和社会保险补贴的公益性岗位，其劳动合同不适用劳动合同法有关无固定期限劳动合同的规定以及支付经济补偿的规定。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第十三条　用人单位与劳动者不得在劳动合同法第四十四条规定的劳动合同终止情形之外约定其他的劳动合同终止条件。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第十四条　劳动合同履行地与用人单位注册地不一致的，有关劳动者的最低工资标准、劳动保护、劳动条件、职业危害防护和本地区上年度职工月平均工资标准等事项，按照劳动合同履行地的有关规定执行；用人单位注册地的有关标准高于劳动合同履行地的有关标准，且用人单位与劳动者约定按照用人单位注册地的有关规定执行的，从其约定。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第十五条　劳动者在试用期的工资不得低于本单位相同岗位最低档工资的80%或者不得低于劳动合同约定工资的80%，并不得低于用人单位所在地的最低工资标准。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第十六条　劳动合同法第二十二条第二款规定的培训费用，包括用人单位为了对劳动者进行专业技术培训而支付的有凭证的培训费用、培训期间的差旅费用以及因培训产生的用于该劳动者的其他直接费用。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第十七条　劳动合同期满，但是用人单位与劳动者依照劳动合同法第二十二条的规定约定的服务期尚未到期的，劳动合同应当续延至服务期满；双方另有约定的，从其约定。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\"><strong>　　第三章　劳动合同的解除和终止</strong></span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第十八条　有下列情形之一的，依照劳动合同法规定的条件、程序，劳动者可以与用人单位解除固定期限劳动合同、无固定期限劳动合同或者以完成一定工作任务为期限的劳动合同：</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(一)劳动者与用人单位协商一致的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(二)劳动者提前30日以书面形式通知用人单位的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(三)劳动者在试用期内提前3日通知用人单位的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(四)用人单位未按照劳动合同约定提供劳动保护或者劳动条件的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(五)用人单位未及时足额支付劳动报酬的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(六)用人单位未依法为劳动者缴纳社会保险费的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(七)用人单位的规章制度违反法律、法规的规定，损害劳动者权益的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(八)用人单位以欺诈、胁迫的手段或者乘人之危，使劳动者在违背真实意思的情况下订立或者变更劳动合同的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(九)用人单位在劳动合同中免除自己的法定责任、排除劳动者权利的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(十)用人单位违反法律、行政法规强制性规定的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(十一)用人单位以暴力、威胁或者非法限制人身自由的手段强迫劳动者劳动的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(十二)用人单位违章指挥、强令冒险作业危及劳动者人身安全的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(十三)法律、行政法规规定劳动者可以解除劳动合同的其他情形。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第十九条　有下列情形之一的，依照劳动合同法规定的条件、程序，用人单位可以与劳动者解除固定期限劳动合同、无固定期限劳动合同或者以完成一定工作任务为期限的劳动合同：</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(一)用人单位与劳动者协商一致的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(二)劳动者在试用期间被证明不符合录用条件的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(三)劳动者严重违反用人单位的规章制度的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(四)劳动者严重失职，营私舞弊，给用人单位造成重大损害的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(五)劳动者同时与其他用人单位建立劳动关系，对完成本单位的工作任务造成严重影响，或者经用人单位提出，拒不改正的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(六)劳动者以欺诈、胁迫的手段或者乘人之危，使用人单位在违背真实意思的情况下订立或者变更劳动合同的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(七)劳动者被依法追究刑事责任的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(八)劳动者患病或者非因工负伤，在规定的医疗期满后不能从事原工作，也不能从事由用人单位另行安排的工作的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(九)劳动者不能胜任工作，经过培训或者调整工作岗位，仍不能胜任工作的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(十)劳动合同订立时所依据的客观情况发生重大变化，致使劳动合同无法履行，经用人单位与劳动者协商，未能就变更劳动合同内容达成协议的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(十一)用人单位依照企业破产法规定进行重整的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(十二)用人单位生产经营发生严重困难的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(十三)企业转产、重大技术革新或者经营方式调整，经变更劳动合同后，仍需裁减人员的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(十四)其他因劳动合同订立时所依据的客观经济情况发生重大变化，致使劳动合同无法履行的。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第二十条　用人单位依照劳动合同法第四十条的规定，选择额外支付劳动者一个月工资解除劳动合同的，其额外支付的工资应当按照该劳动者上一个月的工资标准确定。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第二十一条　劳动者达到法定退休年龄的，劳动合同终止。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第二十二条　以完成一定工作任务为期限的劳动合同因任务完成而终止的，用人单位应当依照劳动合同法第四十七条的规定向劳动者支付经济补偿。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第二十三条　用人单位依法终止工伤职工的劳动合同的，除依照劳动合同法第四十七条的规定支付经济补偿外，还应当依照国家有关工伤保险的规定支付一次性工伤医疗补助金和伤残就业补助金。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第二十四条　用人单位出具的解除、终止劳动合同的证明，应当写明劳动合同期限、解除或者终止劳动合同的日期、工作岗位、在本单位的工作年限。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第二十五条　用人单位违反劳动合同法的规定解除或者终止劳动合同，依照劳动合同法第八十七条的规定支付了赔偿金的，不再支付经济补偿。赔偿金的计算年限自用工之日起计算。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第二十六条　用人单位与劳动者约定了服务期，劳动者依照劳动合同法第三十八条的规定解除劳动合同的，不属于违反服务期的约定，用人单位不得要求劳动者支付违约金。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　有下列情形之一，用人单位与劳动者解除约定服务期的劳动合同的，劳动者应当按照劳动合同的约定向用人单位支付违约金：</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(一)劳动者严重违反用人单位的规章制度的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(二)劳动者严重失职，营私舞弊，给用人单位造成重大损害的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(三)劳动者同时与其他用人单位建立劳动关系，对完成本单位的工作任务造成严重影响，或者经用人单位提出，拒不改正的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(四)劳动者以欺诈、胁迫的手段或者乘人之危，使用人单位在违背真实意思的情况下订立或者变更劳动合同的；</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　(五)劳动者被依法追究刑事责任的。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第二十七条　劳动合同法第四十七条规定的经济补偿的月工资按照劳动者应得工资计算，包括计时工资或者计件工资以及奖金、津贴和补贴等货币性收入。劳动者在劳动合同解除或者终止前12个月的平均工资低于当地最低工资标准的，按照当地最低工资标准计算。劳动者工作不满12个月的，按照实际工作的月数计算平均工资。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\"><strong>　　第四章　劳务派遣特别规定</strong></span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第二十八　条用人单位或者其所属单位出资或者合伙设立的劳务派遣单位，向本单位或者所属单位派遣劳动者的，属于劳动合同法第六十七条规定的不得设立的劳务派遣单位。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第二十九条　用工单位应当履行劳动合同法第六十二条规定的义务，维护被派遣劳动者的合法权益。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">第三十条　劳务派遣单位不得以非全日制用工形式招用被派遣劳动者。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第三十一条　劳务派遣单位或者被派遣劳动者依法解除、终止劳动合同的经济补偿，依照劳动合同法第四十六条、第四十七条的规定执行。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第三十二条　劳务派遣单位违法解除或者终止被派遣劳动者的劳动合同的，依照劳动合同法第四十八条的规定执行。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\"><strong>　　第五章　法律责任</strong></span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第三十三条　用人单位违反劳动合同法有关建立职工名册规定的，由劳动行政部门责令限期改正；逾期不改正的，由劳动行政部门处2000元以上2万元以下的罚款。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第三十四条　用人单位依照劳动合同法的规定应当向劳动者每月支付两倍的工资或者应当向劳动者支付赔偿金而未支付的，劳动行政部门应当责令用人单位支付。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第三十五条　用工单位违反劳动合同法和本条例有关劳务派遣规定的，由劳动行政部门和其他有关主管部门责令改正；情节严重的，以每位被派遣劳动者1000元以上5000元以下的标准处以罚款；给被派遣劳动者造成损害的，劳务派遣单位和用工单位承担连带赔偿责任。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\"><strong>　　第六章　附则</strong></span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第三十六条　对违反劳动合同法和本条例的行为的投诉、举报，县级以上地方人民政府劳动行政部门依照《劳动保障监察条例》的规定处理。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第三十七条　劳动者与用人单位因订立、履行、变更、解除或者终止劳动合同发生争议的，依照《中华人民共和国劳动争议调解仲裁法》的规定处理。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　第三十八条　本条例自公布之日起施行。</span></span></p>',10,0,'管理分院',NULL,NULL,0),(38,'yangchun',100,'专家教你做简历','46','2010-03-10 22:30:21','<p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 关于简历的写作，已经有大量的书籍和忠告，你一定听过诸如&quot;简历长度不应超过一页&quot;、&quot;不要用有颜色的纸&quot;等等建议，而事实上并没有放之四海皆准的统一标准，但你写自己的个人简历时应当根据以下指引：</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　1.将你的资料，资历证明等列出时行文力求简洁、清楚、易于阅读。将你所做的每一件事都仔细说明不一定好，否则简历将流于琐碎繁冗，那些有招聘意愿的雇主可能不会花时间去阅读。写简历一条有效原则是：只列出你过去十年的工作经历。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　2.简历的主要目的是就你所希望的职位推销自己，所以应清楚指出你所需要的工作以及为什么认为自己够格。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　3.刚毕业的大学生需要列出大学期间的所有实习、义务活动、以及各种各样参与过的活动，因为任何招聘都倾向于和该职位相关的大学专业。绝大部分的大学毕业生都缺乏工作经历，所以在应聘职位下面直接写上&quot;相关经验&quot;，然后着重描写你所有的经验。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　4.教育专业毕业的学生会有教学生的经验，可以在&quot;相关经验&quot;的对面写上标题&quot;教学经验&quot;以强调你的教学经验。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　5.没有任何经验的毕业生应该在&quot;学历&quot;一栏下更详细地加以描述并列出大学的相关课程使得简历更充实。如果职位更倾向于大学本身的话，仅仅在&quot;其他经验&quot;下把工作过的地方和职务列出，但不要写出细节，这表明你过去的职责。并且不要忘记实习往往为人提供最初的工作经验。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　6.只有一点经验的求职者，譬如说一个刚刚养家几年的主妇，可能会觉得没什么可以在简历上写的东西。这种情况应该重点强调养育一个孩子以及管理家务事所需要的责任心。</span></span></p><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">　　7.年纪较长的申请人与其虚泛地说自己的年资，不如着重描写自己的工作成就。如果在不同的公司有不同的职务，但基本职责相通的话，就没必要重复工作性质。在列出工作过的公司名称和职位下面加上一段你最近职位的简要说明就好。这种做法的目的是使人有兴趣读你在简历中写的东西。</span></span></p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p>',15,0,'管理分院',NULL,NULL,0),(39,'yangchun',100,'2011年考研备战时间表','46','2010-03-10 22:34:06','<div align=\"center\"><table cellspacing=\"0\" cellpadding=\"0\" width=\"455\" border=\"1\">    <tbody>        <tr>            <td valign=\"top\" width=\"48\">            <div>考研<br />            阶段</div>            </td>            <td valign=\"top\" width=\"106\">            <div>时间</div>            </td>            <td valign=\"top\" width=\"301\">            <div>内容</div>            </td>        </tr>        <tr>            <td valign=\"top\" width=\"48\" rowspan=\"2\">            <div>准备<br />            阶段</div>            </td>            <td valign=\"top\" width=\"106\">            <div>2010年1月-2月</div>            </td>            <td valign=\"top\" width=\"301\">            <div>1.搜集考研资料，确定考研目标，听考研形势讲座。<br />            2.选择专业，全面了解所报专业的信息，准备复习。<br />            3.可参加寒假基础班系统学习数学、英语等科目。</div>            </td>        </tr>        <tr>            <td valign=\"top\" width=\"106\">            <div>2010年 2月-3月</div>            </td>            <td valign=\"top\" width=\"301\">            <div>听最新的考研讲座，购买考研真题，评估自己实力，可参加春季辅导班，制定学习计划。</div>            <div>　</div>            </td>        </tr>        <tr>            <td valign=\"top\" width=\"48\" rowspan=\"2\">            <div>复习<br />            初期<br />            阶段</div>            </td>            <td valign=\"top\" width=\"106\">            <div>2010年 4月-5月</div>            </td>            <td valign=\"top\" width=\"301\">            <div>第一轮复习：可以报一个基础班，特别是数学班和英语班。不要急于做模拟试题，着重于基础的复习。</div>            </td>        </tr>        <tr>            <td valign=\"top\" width=\"106\">            <div>2010年 6月-7月</div>            </td>            <td valign=\"top\" width=\"301\">            <div>全面关注考研公共课的考试大纲，购买最新的辅导用书，准备暑期复习。</div>            </td>        </tr>        <tr>            <td valign=\"top\" width=\"48\" rowspan=\"4\">            <div>强化<br />            提高<br />            阶段</div>            </td>            <td valign=\"top\" width=\"106\">            <div>2010年 7月-8月</div>            </td>            <td valign=\"top\" width=\"301\">            <div>制定一个全面复习计划，开始第二轮复习。重点复习政治、巩固英语和数学，可参加暑期班，做到三门公共课同步提高。 　</div>            </td>        </tr>        <tr>            <td valign=\"top\" width=\"106\">            <div>2010年 9月</div>            </td>            <td valign=\"top\" width=\"301\">            <div>1.关注各招生单位的招生简章和专业计划，购买专业辅导用书，联系导师，获取专业课考试信息。　<br />            2.强化公共课的复习效果，不断完善复习总体结构。</div>            </td>        </tr>        <tr>            <td valign=\"top\" width=\"106\">            <div>2010年 10月</div>            </td>            <td valign=\"top\" width=\"301\">            <div>确定十一黄金周复习计划，对前两个阶段的复习进行总结、梳理、查缺补漏。同时，开始专业课的复习。</div>            </td>        </tr>        <tr>            <td valign=\"top\" width=\"106\">            <div>2010年 10月--11月</div>            </td>            <td valign=\"top\" width=\"301\">            <div>1.研究生考试网上报名工作开始，谨慎填报，牢记报名信息。<br />            2.研究生考试报名工作确认开始，考生到指定的地点进行现场确认，缴费并照相。　</div>            </td>        </tr>        <tr>            <td valign=\"top\" width=\"48\">            <div>冲刺<br />            阶段</div>            </td>            <td valign=\"top\" width=\"106\">            <div>2010年 11月下旬-- 12月</div>            </td>            <td valign=\"top\" width=\"301\">            <div>第三轮复习：进行全面冲刺复习，可参加<span><span><u><font color=\"#0000ff\">政治</font></u></span></span>、<span><span><u><font color=\"#0000ff\">英语</font></u></span></span>、<span><span><u><font color=\"#0000ff\">数学</font></u></span></span>、<span><span><u><font color=\"#0000ff\">专业课</font></u></span></span>冲刺班进行查缺补漏。进行模拟实战训练，最后梳理考点。</div>            </td>        </tr>        <tr>            <td valign=\"top\" width=\"48\">            <div>初试<br />            阶段</div>            </td>            <td valign=\"top\" width=\"106\">            <div>2011年 1月</div>            </td>            <td valign=\"top\" width=\"301\">            <div>调整心态，准备考试。熟悉考试环境。</div>            </td>        </tr>        <tr>            <td valign=\"top\" width=\"48\" rowspan=\"3\">            <div><u><font color=\"#800080\"><span><span>复试</span><br />            <span>调剂</span></span>阶段</font></u><br />            &nbsp;</div>            </td>            <td valign=\"top\" width=\"106\">            <div>2011年 2月</div>            </td>            <td valign=\"top\" width=\"301\">            <div>放松心情，查询初试成绩。</div>            </td>        </tr>        <tr>            <td valign=\"top\" width=\"106\">            <div>2011年 3月--4月</div>            </td>            <td valign=\"top\" width=\"301\">            <div>关注复试分数线，关注所报院校及专业的最新动态，准备复试。<br />            如果达到复试分数线，却没有被通知复试，可联系相关专业院校准备调剂。</div>            </td>        </tr>        <tr>            <td valign=\"top\" width=\"106\">            <div>2011年 4月--5月</div>            <div>&nbsp;</div>            </td>            <td valign=\"top\" width=\"301\">            <div>准备复试，联系招生单位，关注复试成绩。</div>            </td>        </tr>        <tr>            <td valign=\"top\" width=\"48\" rowspan=\"2\">            <div>录取<br />            报到<br />            阶段</div>            </td>            <td valign=\"top\" width=\"106\">            <div>2011年 6月-7月</div>            </td>            <td valign=\"top\" width=\"301\">            <div>关注录取通知书</div>            </td>        </tr>        <tr>            <td valign=\"top\" width=\"106\">            <div>2011年 9月</div>            </td>            <td valign=\"top\" width=\"301\">            <div>报到</div>            </td>        </tr>    </tbody></table></div><p>&nbsp;</p>',16,0,'管理分院',NULL,NULL,0),(40,'yangchun',100,'2011年公务员考试报名时间汇总 ','46','2010-03-10 22:39:25','<div class=\"fs-user-action-body\" id=\"post0\"><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">2011年公务员考试时间（各地） </span></span><div class=\"fs-newline\">&nbsp;</div><p><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">每年的公务员考试时间一般都不一样，&nbsp;&nbsp;&nbsp; 2010-2011年地方公务员考试时间安排 </span></span></p><p>&nbsp;</p><div class=\"fs-newline\">&nbsp;</div><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">【北京】 </span></span><div class=\"fs-newline\">&nbsp;</div><span style=\"font-size: medium\"><span style=\"font-family: Tahoma\">报名时间：2010年9月23日8:00至9月30日11:00 </span></span><div class=\"fs-newline\">&nbsp;</div><span style=\"font-size: medium\"><span style=\"font-family: Tahom